import { useRouter } from 'next/router';
import Layout from '../../components/Layout';

const Content = ({ router }) => (
  <div>
    <h1>{router.query.id}</h1>
    <p>This is the blog post content</p>
  </div>
);

export default () => {
  const router = useRouter();
  return (
    <Layout>
      <Content router={router} />
    </Layout>
  );
};
